import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { DepartmentInfoService } from 'src/app/shared/department-info.service';


@Component({
  selector: 'app-admin-department',
  templateUrl: './department.component.html',
  styleUrls: ['./department.component.css']
})
export class AdminDepartmentComponent implements OnInit {
  constructor(public objs: DepartmentInfoService, public cdRef : ChangeDetectorRef){}
  ngOnInit(): void {
    this.objs.getDepartmentInfoList();
  }

  fillForm(selectedDep)
  {
    this.objs.depData = Object.assign({}, selectedDep)
  }

  createNew() {
    this.objs.depData = { DepartmentId: "", DepartmentName: "", ContactNumber: "" };
  }


  onDelete(depId)
  {
    if(confirm('Are you sure you want to delete this Department ?'))
    {
      this.objs.delDepartmentInfo(depId).subscribe(
        res=>{this.objs.getDepartmentInfoList()
        },
        err=>{alert("ERROR OCUURED " + err);
      }
      )
    }
  }

}
<div><button type="button" (click)="createNew()" routerLink="/departmentReg">Create New</button></div>



<table class="table table-hover">
    <thead>
        <tr>
            <th scope="col">DepartmentID</th>
            <th scope="col">Department Name</th>
            <th scope="col">Contact Number</th>
            <th scope="col">Update</th>
            <th scope="col">Delete</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let d of objs.depList">
            <td>{{d.DepartmentId}}</td> 
            <td>{{d.DepartmentName}}</td>
            <td>{{d.ContactNumber}}</td>
            <td>
                <button type="button" id="viewDepartment" routerLink="/departmentReg" (click)="fillForm(d)">Edit</button>
            </td>
            <td>
                <button type="button" id="viewDepartment" (click)="onDelete(d.DepartmentId)">Delete</button>
            </td>
        </tr>
    </tbody>
</table>
