import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { DepartmentInfoService } from 'src/app/shared/department-info.service';

@Component({
  selector: 'app-department-reg',
  templateUrl: './department-reg.component.html',
  styleUrls: ['./department-reg.component.css']
})
export class DepartmentRegComponent implements OnInit {

  dId:any=""
  data:{}
  constructor(public objservice: DepartmentInfoService,private router:Router) { 
    this.dId = this.router.getCurrentNavigation().extras.state

    this.dId = this.dId.id
  }

  ngOnInit() {
    this.resetForm();
    this.getDep();


  }
getDep(){
  this.objservice.getDepartmentInfoListById(this.dId).subscribe(res => {

    this.data = res
    console.log(res)
  },
    err => { alert('Error!!!' + err); })
}
  resetForm(form?: NgForm) {
    if (form != null) {
      form.form.reset();
    }
    else {
      this.objservice.depData = { DepartmentId: '', DepartmentName: '', ContactNumber: '' }
    }
  }

  onSubmit(form: NgForm) {
    if (this.objservice.depData.DepartmentId == '') {
      this.insertRecord(form);
    }
    else {
      this.updateRecord(form);
    }
  }

  insertRecord(form: NgForm) {
    this.objservice.postDepartmentInfo().subscribe(res => {
      this.resetForm(form);
      this.objservice.getDepartmentInfoList();
      alert('Passport Registeration Success!!!');
    },
      err => { alert('Error!!!' + err); })
  }

  updateRecord(form: NgForm) {
    this.objservice.putDepartmentInfo().subscribe(
      res => {
        this.resetForm();
        this.objservice.getDepartmentInfoList();
        alert('Department Record updated!!!');
      },
      err => {
        alert('Error!!!' + err);
      }
    )
  }

}
