import { Component, OnInit } from '@angular/core';
import { DepartmentInfoService } from 'src/app/shared/department-info.service';

@Component({
  selector: 'app-admin-department',
  templateUrl: './department.component.html',
  styleUrls: ['./department.component.css']
})
export class AdminDepartmentComponent implements OnInit {
  constructor(public objs: DepartmentInfoService){}
  ngOnInit(): void {
    this.objs.getDepartmentInfoList();
  }

  onDelete(dId)
  {
    if(confirm('Are you want to delete this departmen?'))
    {
      this.objs.delDepartmentInfo(dId).subscribe(
        res=>{this.objs.getDepartmentInfoList()
        },
        err=>{alert("Error Occured "+err)}
      )
      
    }
  }

}
<table class="table table-hover">
    <thead>
        <tr>
            <th scope="col">DepartmentID</th>
            <th scope="col">Department Name</th>
            <th scope="col">Contact Number</th>
            <th scope="col">Update</th>
            <th scope="col">Delete</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let d of objs.depList">
            <td>{{d.DepartmentId}}</td> 
            <td>{{d.DepartmentName}}</td>
            <td>{{d.ContactNumber}}</td>
            <td>
                <button type="button" id="viewDepartment" (click)="onUpdate(d.DepartmentID)">Edit</button>
            </td>
            <td>
                <button type="button" id="viewDepartment" (click)="onDelete(d.DepartmentId)">Delete</button>
            </td>
        </tr>
    </tbody>
</table>
