import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
    constructor(private router:Router) {}

    username:string = "chaitanya";
    password:string = "password";

    navigateToLink(){
      const user = (document.getElementById('username1') as HTMLInputElement).value;
      const pass = (document.getElementById('password1') as HTMLInputElement).value;

      if(this.username == user && this.password== pass){
        this.router.navigate(['student_dashboard']);
      }
      else{
        alert("Invalid Credential");
      }

    }


}

<body>
    <!-- Header -->
    <header class="bg-dark text-white text-center py-4">
        <h1>AdvanceLink Institute</h1>
    </header>

    <!-- Page content -->
    <div class="container mt-5">
        <div class="row justify-content-center">
            <div class="col-md-6">
                <h2 class="mb-4">University Login</h2>
                <form>
                    <div class="mb-3">
                        <label for="username" class="form-label">Username</label>
                        <input type="text" class="form-control" id="username" required>
                    </div>
                    <div class="mb-3">
                        <label for="password" class="form-label">Password</label>
                        <input type="password" class="form-control" id="password" required>
                    </div>
                    <div class="mb-3">
                        <label for="userType" class="form-label">User Type</label>
                        <select class="form-select" id="userType" required>
                            <option value="">Select User Type</option>
                            <option value="admin">Admin</option>
                            <option value="student">Student</option>
                            <option value="faculty">Faculty</option>
                        </select>
                    </div>
                    <button type="submit" class="btn btn-dark" (click)=Authentication()>Login</button>
                </form>
            </div>
        </div>
    </div>

    <!-- Footer -->
    <footer class="bg-light text-center py-3">
        <p>&copy; 2023 AdvanceLink Institute. All rights reserved.</p>
    </footer>
