import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { DepartmentInfoService } from 'src/app/shared/department-info.service';

@Component({
  selector: 'app-department-reg',
  templateUrl: './department-reg.component.html',
  styleUrls: ['./department-reg.component.css']
})
export class DepartmentRegComponent implements OnInit {


  constructor(public objservice: DepartmentInfoService,private router:Router) { 
  }

  ngOnInit() {

  }


  resetForm(form?: NgForm) {
    if (form != null) {
      form.form.reset();
    }
    else {
      this.objservice.depData = { DepartmentId: '', DepartmentName: '', ContactNumber: '' }
    }
  }

  onSubmit(form: NgForm) {
    if (this.objservice.depData.DepartmentId == '') {
      this.insertRecord(form);
    }
    else {
      this.updateRecord(form);
    }
  }

  insertRecord(form: NgForm) {
    this.objservice.postDepartmentInfo().subscribe(res => {
      this.resetForm(form);
      this.objservice.getDepartmentInfoList();
      alert('Passport Registeration Success!!!');
    },
      err => { alert('Error!!!' + err); })
  }

  updateRecord(form: NgForm) {
    this.objservice.putDepartmentInfo().subscribe(
      res => {
        this.resetForm();
        this.objservice.getDepartmentInfoList();
        alert('Department Record updated!!!');
      },
      err => {
        alert('Error!!!' + err);
      }
    )
  }

}
<form #form="ngForm" autocomplete="off" (submit)="onSubmit(form)">
    <div class="container">
        <input type="hidden" name="Id" [value]="data.id">
        <div class="form-group">
            <input name="DepartmentId" #DepartmentId="ngModel" [(ngModel)]="data.DepartmentId" 
             placeholder="Department Id" class="form-control"  required>
        </div>
        <div class="form-group">
            <input name="DepartmentName" #DepartmentName="ngModel" [(ngModel)]="data.DepartmentName" 
             placeholder="Department Name" class="form-control" required>
        </div>
        <div class="form-group">
            <input name="ContactNumber" #ContactNumber="ngModel" [(ngModel)]="data.ContactNumber" 
             placeholder="Contact Number" class="form-control" required>
        </div>
        <div class="form-group">
            <button class="btn btn-success btn-lg"  type="submit"
                [disabled]="form.invalid">
                <i class="fa fa-database"></i> Create/Update Department </button>
        </div>
    </div>
</form>
