import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import jwtDecode from 'jwt-decode';
import { CourseRegService } from 'src/app/shared/course-reg.service';
import { StudentInfoService } from 'src/app/shared/student-info.service';

@Component({
  selector: 'app-student-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class StudentCourseComponent implements OnInit {


  constructor(public courseRegService:CourseRegService, public studentService:StudentInfoService,
    public router:Router) {
  }
  ngOnInit(): void {
    const token: any = localStorage.getItem('authToken');
    this.getAvailableCourses();
    
    try{
      const tokenPayload:any = jwtDecode(token);
      this.studentService.getStudentInfo(tokenPayload.AdminId);
    }catch(error) {
      localStorage.clear();
      this.router.navigate(['/']);
    }
    
  }

  getAvailableCourses(){}
}
  







<div class="container Available Courses">
  <h1>Available Courses</h1>
</div>



<div class="container RegisteredCourses">
  <h1>Registered Courses</h1>
  <table class="table table-hover">
      <thead>
          <tr>
              <th scope="col">Course ID</th>
              <th scope="col">Course Code</th>
              <th scope="col">Course Name</th>
              <th scope="col">Credits</th>
              <th scope="col">Syllabus</th>
              <th scope="col">Description</th>
          </tr>
      </thead>
      <tbody>
          <tr *ngFor="let c of courseRegService.CourseRegAddOnList;">
              <td>{{c.CourseId}}</td> 
              <td>{{c.CourseCode}}</td>
              <td >{{c.CourseName}}</td>
              <td>{{c.Credits}}</td> 
              <td>{{c.Syllabus}}</td>
              <td >{{c.Description}}</td>
          </tr>
      </tbody>
    </table>
</div>
