import { Component, OnInit } from '@angular/core';
import { BranchInfoService } from 'src/app/branch-info.service';

@Component({
  selector: 'app-branch',
  templateUrl: './branch.component.html',
  styleUrls: ['./branch.component.css']
})
export class BranchComponent implements OnInit {
  constructor(public objs: BranchInfoService) {}

  ngOnInit(): void {
    this.objs.getBranchesWithDepartment();
    console.log(this.objs.branchWithDepList)
  }

  fillForm(selectedDep)
  {
    this.objs.branchWithDepData = Object.assign({}, selectedDep)
  }

  createNew() {
    this.objs.branchWithDepData = { BranchId: "", BranchName: "", DepartmentName: "" };
  }


  onDelete(depId)
  {
    if(confirm('Are you sure you want to delete this Department ?'))
    {
      this.objs.delBranchInfo(depId).subscribe(
        res=>{
          alert('Department Deleted');
          this.objs.getBranchInfoList();
          
        },
        err=>{alert("ERROR OCUURED " + err);
      }
      )
    }
  }
}
<table class="table table-hover">
  <thead>
      <tr>
          <th scope="col">BranchID</th>
          <th scope="col">Branch Name</th>
          <th scope="col">Department Name</th>
          <th scope="col">Update</th>
          <th scope="col">Delete</th>
      </tr>
  </thead>
  <tbody>
      <tr *ngFor="let b of objs.branchWithDepList">
          <td>{{b.BranchId}}</td> 
          <td>{{b.BranchName}}</td>
          <td >{{b.DepartmentName}}</td>
          <td>
              <button type="button" id="EditBranch" (click)="fillForm(b)" routerLink="/branchReg">Edit</button>
          </td>
          <td>
              <button type="button" id="DeleteBranch">Delete</button>
          </td>
      </tr>
  </tbody>
</table>
