import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import jwtDecode from 'jwt-decode';
import { CourseInfoService } from 'src/app/shared/course-info.service';
import { CourseReg } from 'src/app/shared/course-reg.model';
import { CourseRegService } from 'src/app/shared/course-reg.service';

@Component({
  selector: 'app-reg-course',
  templateUrl: './reg-course.component.html',
  styleUrls: ['./reg-course.component.css']
})
export class RegCourseComponent implements OnInit {
  tokenPayload:any;
  constructor(public objService:CourseRegService, public courseService:CourseInfoService){}
  ngOnInit(): void {
    this.resetForm();
    const token: any = localStorage.getItem('authToken');
    tokenPayload = jwtDecode(token);

  }

  resetForm(form?:NgForm){
    if(form!=null)
    {
      form.form.reset();
    }
    else{
      this.objService.CourseRegData = {StudentId:"", CourseId:"", DateOfReg: new Date()};
    }
  }


}
<div class="container">
    <div class="row justify-content-center">
      <div class="col-md-6">
        <form #form="ngForm" autocomplete="off" (submit)="onSubmit(form)">


          <div class="form-group">
            <label for="BranchId">Student Id</label>
            <div class="input-group">
              <div class="input-group-prepend">
                <span class="input-group-text bg-white">
                  <i class="fa fa-user-circle-o" aria-hidden="true" [class.green-icon]="StudentId.valid"
                    [class.red-icon]="StudentId.invalid && StudentId.touched"></i>
                </span>
              </div>
              <input name="StudentId" #StudentId="ngModel" [(ngModel)]="objService.branchData.StudentId" id="StudentId"
                class="form-control" placeholder="Student Id" required>
            </div>
          </div>

          <div class="form-group">
            <label for="BranchName">Course Id</label>
            <div class="input-group">
              <div class="input-group-prepend">
                <span class="input-group-text bg-white">
                  <i class="fa fa-user-circle-o" aria-hidden="true" [class.green-icon]="CourseId.valid"
                    [class.red-icon]="CourseId.invalid && CourseId.touched"></i>
                </span>
              </div>
              <input name="CourseId" #CourseId="ngModel" [(ngModel)]="objService.branchData.CourseId"
                id="CourseId" class="form-control" placeholder="Course Name" required>
            </div>
          </div>


          <div class="form-group">
            <div class="input-group">
                <div class="input-group-prepend">
                    <div class="input-group-text bg-white">
                        <i class="fa fa-diamond" aria-hidden="true" [class.green-icon]="DateOfReg.valid"
                            [class.red-icon]="DateOfReg.invalid && DateOfReg.touched"></i>
                        <input type="date" name="DateOfReg" #DateOfReg="ngModel" [(ngModel)]="objService.StudentData.DateOfReg" placeholder="DateOfReg"
                            class="form-control" required>
                    </div>
                </div>
            </div>
        </div>


          <div class="form-group">
            <button class="btn btn-success btn-lg" type="submit" [disabled]="form.invalid">
              <i class="fa fa-database"></i> Registered for Course
            </button>
          </div>

        </form>
      </div>
    </div>
  </div>
