import { Component, OnInit } from '@angular/core';
import { BranchInfoService } from 'src/app/branch-info.service';

@Component({
  selector: 'app-branch',
  templateUrl: './branch.component.html',
  styleUrls: ['./branch.component.css']
})
export class BranchComponent implements OnInit {
  constructor(public objs: BranchInfoService) {}

  ngOnInit(): void {
    this.objs.getBranchesWithDepartment();
    this.objs.getBranchInfoList();
  }

  fillForm(selectedBranch)
  {
    this.objs.branchData = Object.assign({}, selectedBranch)
  }

  createNew() {
    this.objs.branchData = { BranchId: "", BranchName: "", DepartmentId: "" };
  }


  onDelete(depId)
  {
    if(confirm('Are you sure you want to delete this Department ?'))
    {
      this.objs.delBranchInfo(depId).subscribe(
        res=>{
          alert('Department Deleted');
          this.objs.getBranchInfoList();
          
        },
        err=>{alert("ERROR OCUURED " + err);
      }
      )
    }
  }
}
<div><button type="button" (click)="createNew()" routerLink="/departmentReg">Create New</button></div>

<table class="table table-hover">
  <thead>
      <tr>
          <th scope="col">BranchID</th>
          <th scope="col">Branch Name</th>
          <th scope="col">Department Name</th>
          <th scope="col">Update</th>
          <th scope="col">Delete</th>
      </tr>
  </thead>
  <tbody>
      <tr *ngFor="let b of objs.branchWithDepList; let i=index">
          <td>{{b.BranchId}}</td> 
          <td>{{b.BranchName}}</td>
          <td >{{b.DepartmentName}}</td>
          <td>
              <button type="button" id="EditBranch" (click)="fillForm(objs.branchList[i])" routerLink="/branchReg">Edit</button>
          </td>
          <td>
              <button type="button" id="DeleteBranch" (click)="onDelete(b.BranchId)">Delete</button>
          </td>
      </tr>
  </tbody>
</table>
