import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http'
import { Router } from '@angular/router';
@Injectable({
  providedIn: 'root'
})
export class AuthenticationService {
  constructor(private http: HttpClient, private router:Router){}

  authenticate(username:string, password:string, userType:string):void {
    this.http.post('http://localhost:5175/api/Users', {username, password, userType}).subscribe(
      (response: any) => {
        if(userType === 'Admin') {
          this.router.navigate(['/admin_dashboard']);
        }
        else if(userType === 'Student') {
          this.router.navigate(['/student_dashboard']);
        }
        else if(userType === 'Faculty') {
          this.router.navigate(['/faculty_dashboard']);
        }
      },
      (erro: any) => {
        console.error('Authentication failed: ', console.error());
      }
    );
  }
}
