import { Component, OnInit } from '@angular/core';
import { CourseRegService } from 'src/app/shared/course-reg.service';
import jwtDecode from 'jwt-decode';
import { Router } from '@angular/router';

@Component({
  selector: 'app-student-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class StudentDashboardComponent implements OnInit{

  constructor(public courseRegService:CourseRegService, public router:Router){}
  ngOnInit(): void {
    const token: any = localStorage.getItem('authToken');
    
    try{
      const tokenPayload:any = jwtDecode(token);
      console.log(tokenPayload.AdminId);
      this.courseRegService.getCourseRegListByStudentId(tokenPayload.AdminId);
      console.log(this.courseRegService.CourseRegAddOnList)
    }catch(error) {
      localStorage.clear();
      this.router.navigate(['/']);
    }
  }

}
<div class="container RegisteredCourses">
    <h1>Registered Courses</h1>
    <table class="table table-hover">
        <thead>
            <tr>
                <th scope="col">Course ID</th>
                <th scope="col">Course Code</th>
                <th scope="col">Course Name</th>
                <th scope="col">Credits</th>
                <th scope="col">Syllabus</th>
                <th scope="col">Description</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let c of courseRegService.CourseRegAddOnList;">
                <td>{{c.CourseId}}</td> 
                <td>{{c.CourseCode}}</td>
                <td >{{c.CourseName}}</td>
                <td>{{c.Credits}}</td> 
                <td>{{c.Syllabus}}</td>
                <td >{{c.Description}}</td>
            </tr>
        </tbody>
      </table>
</div>

<div class="ExamResult container">
    <table class="table table-hover">
        <thead>
            <tr>
                <th scope="col">Examination ID</th>
                <th scope="col">Marks Obtained</th>
                <th scope="col">Grade Name</th>
            </tr>
        </thead>
        <tbody>
            <!-- <tr *ngFor="let c of objs.CourseAddOnList;">
                <td>{{r.ExamId}}</td> 
                <td>{{r.MarksObtained}}</td>
                <td >{{r.Grade}}</td>
            </tr> -->
        </tbody>
      </table>
</div>

<div class="StudentInfo container">

</div>
