import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthenticationService } from '../authentication.service';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
    constructor(private authService: AuthenticationService) {}
  ngOnInit(): void {
    // throw new Error('Method not implemented.');
  }

    Authentication(){
      const username = (document.getElementById('username') as HTMLInputElement).value;
      const password = (document.getElementById('password') as HTMLInputElement).value;
      const userType = (document.getElementById('userType') as HTMLSelectElement).value;

      this.authService.authenticate(username, password, userType);
    }



}

    <!-- Header -->
    <header class="bg-dark text-white text-center py-4">
        <h1>AdvanceLink Institute</h1>
    </header>

    <!-- Page content -->
    <div class="container mt-5">
        <div class="row justify-content-center">
            <div class="col-md-6">
                <h2 class="mb-4">University Login</h2>
                <form>
                    <div class="mb-3">
                        <label for="username" class="form-label">Username</label>
                        <input type="text" class="form-control" id="username" required>
                    </div>
                    <div class="mb-3">
                        <label for="password" class="form-label">Password</label>
                        <input type="password" class="form-control" id="password" required>
                    </div>
                    <div class="mb-3">
                        <label for="userType" class="form-label">User Type</label>
                        <select class="form-select" id="userType" required>
                            <option value="">Select User Type</option>
                            <option value="Admin">Admin</option>
                            <option value="Student">Student</option>
                            <option value="Faculty">Faculty</option>
                        </select>
                    </div>
                    <button type="submit" class="btn btn-dark" (click)=Authentication()>Login</button>
                </form>
            </div>
        </div>
    </div>

    <!-- Footer -->
    <footer class="bg-light text-center py-3">
        <p>&copy; 2023 AdvanceLink Institute. All rights reserved.</p>
    </footer>
